            showMessage(`Usuário "${newCompanyUserUsername}" atualizado com sucesso!`, 'success');
            setEditingCompanyUser(null);
            setNewCompanyUserUsername('');
            setNewCompanyUserPassword('');
            setNewCompanyUserRole('caixa');
