            {/* Caixa Tab Content (Visível apenas para company_admin) */}
            {activeTab === 'caixa' && currentUser.role === 'company_admin' && (
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 w-full max-w-7xl">
                    {/* Products List */}
