/* global __app_id, __initial_auth_token */
import React, { useState, useEffect, useRef } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, signInWithCustomToken, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, addDoc, getDocs, onSnapshot, doc, updateDoc, deleteDoc, query, where } from 'firebase/firestore';

// Firebase configuration and initialization
// Your specific Firebase project configuration is now hardcoded here for local development.
const firebaseConfig = {
    apiKey: "AIzaSyDNIJRlw0mJP349owctGbO58VZWGa0LtQs",
    authDomain: "caixa-1bd0c.firebaseapp.com",
    projectId: "caixa-1bd0c",
    storageBucket: "caixa-1bd0c.firebasestorage.app",
    messagingSenderId: "651091159828",
    appId: "1:651091159828:web:1043424c00f4e71bf97001",
    measurementId: "G-4NJ0F95DVJ"
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

// Global variables for app ID and initial auth token (from Canvas environment)
const appId = typeof __app_id !== 'undefined' ? __app_id : 'local-app-id';
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

// URL base do seu backend Flask
const FLASK_BACKEND_URL = 'http://127.0.0.1:5000';

// Main App Component
const App = () => {
    const [products, setProducts] = useState([]);
    const [cart, setCart] = useState([]);
    const [total, setTotal] = useState(0);
    const [paymentAmount, setPaymentAmount] = useState('');
    const [change, setChange] = useState(0);
    const [difference, setDifference] = useState(0);
    const [paymentMethod, setPaymentMethod] = useState('Dinheiro');
    const [sales, setSales] = useState([]);
    const [activeTab, setActiveTab] = useState('caixa'); // 'caixa', 'produtos', 'relatorios'
    const [newProductName, setNewProductName] = useState('');
    const [newProductValue, setNewProductValue] = useState('');
    const [newProductId, setNewProductId] = useState('');
    const [editingProduct, setEditingProduct] = useState(null);
    const [message, setMessage] = useState('');

    // Estados para autenticação via Flask backend
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [currentUser, setCurrentUser] = useState(null); // Armazena { username, role }
    const [loginUsername, setLoginUsername] = useState('');
    const [loginPassword, setLoginPassword] = useState('');

    const messageTimeoutRef = useRef(null);

    // Function to display messages
    const showMessage = (msg, type = 'success') => {
        setMessage({ text: msg, type });
        if (messageTimeoutRef.current) {
            clearTimeout(messageTimeoutRef.current);
        }
        messageTimeoutRef.current = setTimeout(() => {
            setMessage('');
        }, 3000);
    };

    // Firebase Authentication (agora usa o token personalizado do Flask)
    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            // Este listener é para observar o estado de autenticação do Firebase.
            // O login principal é orquestrado pelo Flask.
            if (user && isLoggedIn && currentUser && user.uid === currentUser.username) {
                // Se o usuário Firebase estiver autenticado e corresponder ao usuário do Flask,
                // significa que o signInWithCustomToken funcionou.
                console.log("Firebase Auth state changed: User is logged in with UID:", user.uid);
            } else if (!user && isLoggedIn) {
                // Se o usuário do Flask estiver logado mas o Firebase Auth não,
                // pode indicar um problema ou que o signInWithCustomToken ainda não foi chamado.
                console.warn("Firebase Auth state changed: User logged in via Flask, but Firebase Auth is not.");
            } else if (user && !isLoggedIn) {
                // Se o Firebase Auth estiver logado mas o Flask não, pode ser um estado residual.
                // REMOVIDO: auth.signOut(); para evitar deslogar prematuramente
                console.warn("Firebase Auth state changed: User logged in via Firebase, but not via Flask. (No auto-logout)");
            }
        });
        return () => unsubscribe();
    }, [isLoggedIn, currentUser]); // Depende do estado de login e do usuário atual

    // Firestore Listeners for Products and Sales (dependem do currentUser do Flask)
    useEffect(() => {
        // Só carrega dados do Firestore se o usuário estiver logado via Flask
        if (!isLoggedIn || !currentUser || !currentUser.username) {
            setProducts([]); // Limpa produtos se não estiver logado
            setSales([]); // Limpa vendas se não estiver logado
            return;
        }

        console.log("Attempting to fetch Firestore data for user:", currentUser.username); // Novo log

        // Products Listener
        const productsCollectionRef = collection(db, `artifacts/${appId}/users/${currentUser.username}/products`);
        const unsubscribeProducts = onSnapshot(productsCollectionRef, (snapshot) => {
            const productsData = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
            setProducts(productsData);
            console.log("Produtos carregados com sucesso!"); // Novo log
        }, (error) => {
            console.error("Erro ao carregar produtos:", error);
            showMessage("Erro ao carregar produtos do Firestore. Verifique as permissões.", "error");
        });

        // Sales Listener
        const salesCollectionRef = collection(db, `artifacts/${appId}/users/${currentUser.username}/sales`);
        const unsubscribeSales = onSnapshot(salesCollectionRef, (snapshot) => {
            const salesData = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
            setSales(salesData);
            console.log("Vendas carregadas com sucesso!"); // Novo log
        }, (error) => {
            console.error("Erro ao carregar vendas:", error);
            showMessage("Erro ao carregar vendas do Firestore. Verifique as permissões.", "error");
        });

        return () => {
            unsubscribeProducts();
            unsubscribeSales();
        };
    }, [isLoggedIn, currentUser, appId]); // Depende do estado de login e do usuário atual

    // Calculate total whenever cart changes
    useEffect(() => {
        const newTotal = cart.reduce((sum, item) => sum + (item.value * item.quantity), 0);
        setTotal(newTotal);
    }, [cart]);

    // Calculate change/difference whenever total or payment amount changes
    useEffect(() => {
        const paid = parseFloat(paymentAmount) || 0;
        if (paid >= total) {
            setChange(paid - total);
            setDifference(0);
        } else {
            setChange(0);
            setDifference(total - paid);
        }
    }, [total, paymentAmount]);

    // Add product to cart
    const addToCart = (product) => {
        const existingItem = cart.find(item => item.id === product.id);
        if (existingItem) {
            setCart(cart.map(item =>
                item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
            ));
        } else {
            setCart([...cart, { ...product, quantity: 1 }]);
        }
        showMessage(`${product.name} adicionado ao carrinho!`);
    };

    // Remove product from cart
    const removeFromCart = (productId) => {
        setCart(cart.filter(item => item.id !== productId));
        showMessage("Item removido do carrinho.", "info");
    };

    // Increase quantity in cart
    const increaseQuantity = (productId) => {
        setCart(cart.map(item =>
            item.id === productId ? { ...item, quantity: item.quantity + 1 } : item
        ));
    };

    // Decrease quantity in cart
    const decreaseQuantity = (productId) => {
        setCart(cart.map(item =>
            item.id === productId && item.quantity > 1 ? { ...item, quantity: item.quantity - 1 } : item
        ).filter(item => item.quantity > 0)); // Remove if quantity becomes 0
    };

    // Finalize sale
    const finalizeSale = async () => {
        if (cart.length === 0) {
            showMessage("O carrinho está vazio!", "error");
            return;
        }
        if (paymentMethod === 'Dinheiro' && parseFloat(paymentAmount) < total) {
            showMessage("Valor pago insuficiente!", "error");
            return;
        }

        const saleData = {
            items: cart.map(item => ({
                productId: item.id,
                name: item.name,
                value: item.value,
                quantity: item.quantity
            })),
            total: total,
            paymentMethod: paymentMethod,
            paymentAmount: parseFloat(paymentAmount) || 0,
            change: change,
            timestamp: new Date(),
            userId: currentUser.username // Usa o username do Flask como userId para o Firestore
        };

        try {
            await addDoc(collection(db, `artifacts/${appId}/users/${currentUser.username}/sales`), saleData);
            showMessage("Venda finalizada com sucesso!");
            setCart([]);
            setPaymentAmount('');
            setChange(0);
            setDifference(0);
            setPaymentMethod('Dinheiro');
        } catch (e) {
            console.error("Erro ao adicionar documento: ", e);
            showMessage("Erro ao finalizar venda.", "error");
        }
    };

    // Product Management Functions
    const handleAddProduct = async () => {
        if (!newProductName || !newProductValue || !newProductId) {
            showMessage("Preencha todos os campos do produto!", "error");
            return;
        }
        if (isNaN(parseFloat(newProductValue))) {
            showMessage("O valor do produto deve ser um número!", "error");
            return;
        }

        const productData = {
            name: newProductName,
            id: newProductId, // Use the user-provided ID
            value: parseFloat(newProductValue)
        };

        try {
            // Check if product ID already exists
            const q = query(collection(db, `artifacts/${appId}/users/${currentUser.username}/products`), where("id", "==", newProductId));
            const querySnapshot = await getDocs(q);
            if (!querySnapshot.empty) {
                showMessage("ID de produto já existe!", "error");
                return;
            }

            await addDoc(collection(db, `artifacts/${appId}/users/${currentUser.username}/products`), productData);
            showMessage("Produto adicionado com sucesso!");
            setNewProductName('');
            setNewProductValue('');
            setNewProductId('');
        } catch (e) {
            console.error("Erro ao adicionar produto: ", e);
            showMessage("Erro ao adicionar produto.", "error");
        }
    };

    const handleEditProduct = (product) => {
        setEditingProduct(product);
        setNewProductName(product.name);
        setNewProductValue(product.value.toString());
        setNewProductId(product.id); // Keep the ID for display, but it's not editable
    };

    const handleUpdateProduct = async () => {
        if (!editingProduct || !newProductName || !newProductValue) {
            showMessage("Preencha todos os campos para atualizar!", "error");
            return;
        }
        if (isNaN(parseFloat(newProductValue))) {
            showMessage("O valor do produto deve ser um número!", "error");
            return;
        }

        const productRef = doc(db, `artifacts/${appId}/users/${currentUser.username}/products`, editingProduct.id);
        try {
            await updateDoc(productRef, {
                name: newProductName,
                value: parseFloat(newProductValue)
            });
            showMessage("Produto atualizado com sucesso!");
            setEditingProduct(null);
            setNewProductName('');
            setNewProductValue('');
            setNewProductId('');
        } catch (e) {
            console.error("Erro ao atualizar produto: ", e);
            showMessage("Erro ao atualizar produto.", "error");
        }
    };

    const handleDeleteProduct = async (productId) => {
        try {
            await deleteDoc(doc(db, `artifacts/${appId}/users/${currentUser.username}/products`, productId));
            showMessage("Produto excluído com sucesso!");
        } catch (e) {
            console.error("Erro ao excluir produto: ", e);
            showMessage("Erro ao excluir produto.", "error");
        }
    };

    const cancelEdit = () => {
        setEditingProduct(null);
        setNewProductName('');
        setNewProductValue('');
        setNewProductId('');
    };

    // Calculate weekly sales report
    const getWeeklySales = () => {
        const oneWeekAgo = new Date();
        oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);

        const weeklySales = sales.filter(sale => {
            const saleDate = sale.timestamp.toDate(); // Convert Firestore Timestamp to Date object
            return saleDate >= oneWeekAgo;
        });

        const salesByDay = {};
        weeklySales.forEach(sale => {
            const saleDate = sale.timestamp.toDate().toLocaleDateString('pt-BR');
            if (!salesByDay[saleDate]) {
                salesByDay[saleDate] = 0;
            }
            salesByDay[saleDate] += sale.total;
        });

        // Sort by date for display
        const sortedSales = Object.entries(salesByDay).sort(([dateA], [dateB]) => {
            const [dayA, monthA, yearA] = dateA.split('/').map(Number);
            const [dayB, monthB, yearB] = dateB.split('/').map(Number);
            const dateObjA = new Date(yearA, monthA - 1, dayA);
            const dateObjB = new Date(yearB, monthB - 1, dayB);
            return dateObjA - dateObjB;
        });

        return sortedSales;
    };

    // --- Funções de Autenticação com o Backend Flask ---
    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`${FLASK_BACKEND_URL}/login`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username: loginUsername, password: loginPassword }),
            });

            const data = await response.json();
            console.log("Dados recebidos do backend:", data); // Log para inspecionar a resposta

            if (response.ok) {
                // Autenticação bem-sucedida no Flask, agora use o token Firebase
                const firebaseToken = data.firebase_token;
                if (firebaseToken) {
                    try {
                        await signInWithCustomToken(auth, firebaseToken); // Autentica no Firebase
                        showMessage(`Login bem-sucedido! Bem-vindo, ${data.username}.`, 'success');
                        setIsLoggedIn(true);
                        setCurrentUser({ username: data.username, role: data.role });
                        setLoginPassword('');
                    } catch (firebaseError) {
                        console.error("Erro ao autenticar no Firebase com token personalizado:", firebaseError);
                        showMessage(`Erro ao conectar ao Firebase: ${firebaseError.message}`, 'error');
                        // Se a autenticação Firebase falhar, não consideramos o usuário logado
                        setIsLoggedIn(false);
                        setCurrentUser(null);
                    }
                } else {
                    showMessage("Erro: Token Firebase não recebido do backend.", "error");
                    console.error("Firebase token missing in Flask response. Full data:", data); // Log mais detalhado
                }
            } else {
                showMessage(`Erro de login: ${data.error || 'Credenciais inválidas'}`, 'error');
            }
        } catch (error) {
            console.error('Erro ao conectar ao backend para login:', error);
            showMessage('Erro ao conectar ao servidor. Verifique se o backend está rodando.', 'error');
        }
    };

    const handleLogout = async () => {
        try {
            await auth.signOut(); // Desloga do Firebase Auth
            setIsLoggedIn(false);
            setCurrentUser(null);
            setLoginUsername('');
            setLoginPassword('');
            setCart([]); // Limpa o carrinho ao deslogar
            setProducts([]); // Limpa produtos exibidos
            setSales([]); // Limpa vendas exibidas
            showMessage("Você foi desconectado.", "info");
        } catch (error) {
            console.error("Erro ao deslogar do Firebase:", error);
            showMessage("Erro ao desconectar.", "error");
        }
    };

    // Renderiza a tela de login se o usuário não estiver logado
    if (!isLoggedIn) {
        return (
            <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4 font-sans flex flex-col items-center justify-center">
                {message && (
                    <div className={`fixed top-4 right-4 p-3 rounded-lg shadow-lg text-white z-50 transition-opacity duration-300 ${message.type === 'success' ? 'bg-green-500' : 'bg-red-500'}`}>
                        {message.text}
                    </div>
                )}
                <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-md">
                    <h2 className="text-3xl font-bold text-gray-800 mb-6 text-center">
                        Login no App de Caixa
                    </h2>
                    <form onSubmit={handleLogin}>
                        <div className="mb-4">
                            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="loginUsername">
                                Nome de Usuário:
                            </label>
                            <input
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                id="loginUsername"
                                type="text"
                                placeholder="Nome de Usuário"
                                value={loginUsername}
                                onChange={(e) => setLoginUsername(e.target.value)}
                                required
                            />
                        </div>
                        <div className="mb-6">
                            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="loginPassword">
                                Senha:
                            </label>
                            <input
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
                                id="loginPassword"
                                type="password"
                                placeholder="********"
                                value={loginPassword}
                                onChange={(e) => setLoginPassword(e.target.value)}
                                required
                            />
                        </div>
                        <div className="flex items-center justify-center"> {/* Centralizado */}
                            <button
                                className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg focus:outline-none focus:shadow-outline transition-colors duration-200"
                                type="submit"
                            >
                                Login
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }

    // Renderiza o aplicativo principal se o usuário estiver logado
    return (
        <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4 font-sans flex flex-col items-center">
            {/* User ID Display */}
            {currentUser && (
                <div className="absolute top-4 left-4 bg-white p-2 rounded-lg shadow-md text-sm text-gray-700">
                    Usuário Logado: <span className="font-semibold">{currentUser.username}</span> (Função: {currentUser.role})
                    <button
                        onClick={handleLogout}
                        className="ml-4 bg-red-500 hover:bg-red-600 text-white text-xs px-2 py-1 rounded-full transition-colors duration-200"
                    >
                        Sair
                    </button>
                </div>
            )}

            {/* Message Box */}
            {message && (
                <div className={`fixed top-4 right-4 p-3 rounded-lg shadow-lg text-white z-50 transition-opacity duration-300 ${message.type === 'success' ? 'bg-green-500' : 'bg-red-500'}`}>
                    {message.text}
                </div>
            )}

            <h1 className="text-4xl font-extrabold text-gray-800 mb-8 mt-4 rounded-xl p-3 bg-white shadow-lg">
                Gerenciador de Caixa
            </h1>

            {/* Navigation Tabs */}
            <div className="flex space-x-4 mb-8 bg-white p-2 rounded-full shadow-md">
                <button
                    onClick={() => setActiveTab('caixa')}
                    className={`px-6 py-3 rounded-full text-lg font-medium transition-all duration-300 ${activeTab === 'caixa' ? 'bg-blue-600 text-white shadow-lg' : 'bg-gray-200 text-gray-700 hover:bg-blue-100'}`}
                >
                    Caixa
                </button>
                <button
                    onClick={() => setActiveTab('produtos')}
                    className={`px-6 py-3 rounded-full text-lg font-medium transition-all duration-300 ${activeTab === 'produtos' ? 'bg-blue-600 text-white shadow-lg' : 'bg-gray-200 text-gray-700 hover:bg-blue-100'}`}
                >
                    Produtos
                </button>
                <button
                    onClick={() => setActiveTab('relatorios')}
                    className={`px-6 py-3 rounded-full text-lg font-medium transition-all duration-300 ${activeTab === 'relatorios' ? 'bg-blue-600 text-white shadow-lg' : 'bg-gray-200 text-gray-700 hover:bg-blue-100'}`}
                >
                    Relatórios
                </button>
            </div>

            {/* Caixa Tab Content */}
            {activeTab === 'caixa' && (
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 w-full max-w-7xl">
                    {/* Products List */}
                    <div className="lg:col-span-1 bg-white p-6 rounded-2xl shadow-xl">
                        <h2 className="text-2xl font-bold text-gray-700 mb-4 pb-2 border-b-2 border-blue-200">
                            Produtos Disponíveis
                        </h2>
                        <div className="max-h-96 overflow-y-auto">
                            {products.length === 0 ? (
                                <p className="text-gray-500">Nenhum produto cadastrado. Vá para a aba "Produtos" para adicionar.</p>
                            ) : (
                                products.map(product => (
                                    <div key={product.id} className="flex justify-between items-center bg-gray-50 p-3 mb-2 rounded-lg shadow-sm">
                                        <div>
                                            <p className="font-semibold text-gray-800">{product.name} (ID: {product.id})</p>
                                            <p className="text-blue-600 font-bold">R$ {product.value.toFixed(2)}</p>
                                        </div>
                                        <button
                                            onClick={() => addToCart(product)}
                                            className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-full transition-colors duration-200 shadow-md"
                                        >
                                            Adicionar
                                        </button>
                                    </div>
                                ))
                            )}
                        </div>
                    </div>

                    {/* Cart and Payment */}
                    <div className="lg:col-span-2 bg-white p-6 rounded-2xl shadow-xl flex flex-col">
                        <h2 className="text-2xl font-bold text-gray-700 mb-4 pb-2 border-b-2 border-blue-200">
                            Carrinho de Compras
                        </h2>
                        <div className="flex-grow max-h-80 overflow-y-auto mb-4">
                            {cart.length === 0 ? (
                                <p className="text-gray-500">Carrinho vazio.</p>
                            ) : (
                                cart.map(item => (
                                    <div key={item.id} className="flex justify-between items-center bg-gray-50 p-3 mb-2 rounded-lg shadow-sm">
                                        <div>
                                            <p className="font-semibold text-gray-800">{item.name}</p>
                                            <p className="text-gray-600">R$ {item.value.toFixed(2)} x {item.quantity}</p>
                                        </div>
                                        <div className="flex items-center space-x-2">
                                            <button
                                                onClick={() => decreaseQuantity(item.id)}
                                                className="bg-red-400 hover:bg-red-500 text-white w-8 h-8 rounded-full flex items-center justify-center text-lg font-bold"
                                            >
                                                -
                                            </button>
                                            <span className="font-bold text-lg">{item.quantity}</span>
                                            <button
                                                onClick={() => increaseQuantity(item.id)}
                                                className="bg-green-400 hover:bg-green-500 text-white w-8 h-8 rounded-full flex items-center justify-center text-lg font-bold"
                                            >
                                                +
                                            </button>
                                            <button
                                                onClick={() => removeFromCart(item.id)}
                                                className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded-full text-sm shadow-md"
                                            >
                                                Remover
                                            </button>
                                        </div>
                                    </div>
                                ))
                            )}
                        </div>

                        <div className="mt-auto pt-4 border-t-2 border-blue-200">
                            <div className="flex justify-between items-center text-2xl font-bold text-gray-800 mb-4">
                                <span>Total:</span>
                                <span>R$ {total.toFixed(2)}</span>
                            </div>

                            <div className="mb-4">
                                <label htmlFor="paymentMethod" className="block text-gray-700 text-lg font-semibold mb-2">Método de Pagamento:</label>
                                <select
                                    id="paymentMethod"
                                    value={paymentMethod}
                                    onChange={(e) => setPaymentMethod(e.target.value)}
                                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500 text-lg"
                                >
                                    <option value="Dinheiro">Dinheiro</option>
                                    <option value="Cartao">Cartão de Crédito/Débito</option>
                                    <option value="Pix">Pix</option>
                                </select>
                            </div>

                            {paymentMethod === 'Dinheiro' && (
                                <div className="mb-4">
                                    <label htmlFor="paymentAmount" className="block text-gray-700 text-lg font-semibold mb-2">Valor Pago:</label>
                                    <input
                                        type="number"
                                        id="paymentAmount"
                                        value={paymentAmount}
                                        onChange={(e) => setPaymentAmount(e.target.value)}
                                        placeholder="R$ 0.00"
                                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500 text-lg"
                                    />
                                </div>
                            )}

                            {paymentMethod === 'Pix' && (
                                <div className="mb-4 p-4 bg-yellow-50 rounded-lg border border-yellow-200 text-yellow-800">
                                    <p className="font-semibold">Funcionalidade Pix:</p>
                                    <p className="text-sm">
                                        Para gerar o QR Code Pix, o aplicativo fará uma requisição ao seu backend Flask.
                                        Seu backend Flask (a rota `/pix/generate`) se comunicaria com a API do Mercado Pago.
                                    </p>
                                    <div className="mt-3 text-center">
                                        {/* Placeholder for QR Code */}
                                        <img
                                            src="https://placehold.co/150x150/E0F2F7/000000?text=QR+Code+Pix"
                                            alt="Placeholder QR Code Pix"
                                            className="mx-auto rounded-lg shadow-md"
                                        />
                                        <button
                                            onClick={() => showMessage("Chamada à API Pix do backend seria feita aqui!", "info")}
                                            className="mt-2 bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-full transition-colors duration-200 shadow-md"
                                        >
                                            Gerar QR Code Pix (Simulado)
                                        </button>
                                    </div>
                                </div>
                            )}

                            <div className="flex justify-between items-center text-xl text-gray-700 mb-2">
                                <span>Diferença:</span>
                                <span className="font-bold text-red-600">R$ {difference.toFixed(2)}</span>
                            </div>
                            <div className="flex justify-between items-center text-xl text-gray-700 mb-4">
                                <span>Troco:</span>
                                <span className="font-bold text-green-600">R$ {change.toFixed(2)}</span>
                            </div>

                            <button
                                onClick={finalizeSale}
                                className="w-full bg-blue-600 hover:bg-blue-700 text-white text-xl font-bold py-4 rounded-xl transition-all duration-300 shadow-lg transform hover:scale-105"
                            >
                                Finalizar Venda
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {/* Products Tab Content */}
            {activeTab === 'produtos' && (
                <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-4xl">
                    <h2 className="text-3xl font-bold text-gray-700 mb-6 pb-3 border-b-2 border-blue-200">
                        {editingProduct ? 'Editar Produto' : 'Adicionar Novo Produto'}
                    </h2>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                        <div>
                            <label htmlFor="newProductId" className="block text-gray-700 font-semibold mb-2">ID do Produto:</label>
                            <input
                                type="text"
                                id="newProductId"
                                value={newProductId}
                                onChange={(e) => setNewProductId(e.target.value)}
                                placeholder="Ex: PROD001"
                                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
                                disabled={!!editingProduct} // Disable ID input when editing
                            />
                        </div>
                        <div>
                            <label htmlFor="newProductName" className="block text-gray-700 font-semibold mb-2">Nome do Produto:</label>
                            <input
                                type="text"
                                id="newProductName"
                                value={newProductName}
                                onChange={(e) => setNewProductName(e.target.value)}
                                placeholder="Ex: Refrigerante Lata"
                                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
                            />
                        </div>
                        <div>
                            <label htmlFor="newProductValue" className="block text-gray-700 font-semibold mb-2">Valor (R$):</label>
                            <input
                                type="number"
                                id="newProductValue"
                                value={newProductValue}
                                onChange={(e) => setNewProductValue(e.target.value)}
                                placeholder="Ex: 5.50"
                                step="0.01"
                                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
                            />
                        </div>
                    </div>
                    <div className="flex justify-end space-x-4">
                        {editingProduct ? (
                            <>
                                <button
                                    onClick={handleUpdateProduct}
                                    className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-xl font-bold transition-all duration-300 shadow-md transform hover:scale-105"
                                >
                                    Atualizar Produto
                                </button>
                                <button
                                    onClick={cancelEdit}
                                    className="bg-gray-500 hover:bg-gray-600 text-white px-6 py-3 rounded-xl font-bold transition-all duration-300 shadow-md transform hover:scale-105"
                                >
                                    Cancelar
                                </button>
                            </>
                        ) : (
                            <button
                                onClick={handleAddProduct}
                                className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-xl font-bold transition-all duration-300 shadow-md transform hover:scale-105"
                            >
                                Adicionar Produto
                            </button>
                        )}
                    </div>

                    <h3 className="text-2xl font-bold text-gray-700 mt-10 mb-4 pb-2 border-b-2 border-blue-200">
                        Lista de Produtos
                    </h3>
                    <div className="max-h-96 overflow-y-auto">
                        {products.length === 0 ? (
                            <p className="text-gray-500">Nenhum produto cadastrado.</p>
                        ) : (
                            <ul className="space-y-3">
                                {products.map(product => (
                                    <li key={product.id} className="flex justify-between items-center bg-gray-50 p-4 rounded-lg shadow-sm">
                                        <div>
                                            <p className="font-semibold text-gray-800 text-lg">{product.name} (ID: {product.id})</p>
                                            <p className="text-blue-600 font-bold text-xl">R$ {product.value.toFixed(2)}</p>
                                        </div>
                                        <div className="flex space-x-3">
                                            <button
                                                onClick={() => handleEditProduct(product)}
                                                className="bg-yellow-500 hover:bg-yellow-600 text-white px-4 py-2 rounded-full transition-colors duration-200 shadow-md"
                                            >
                                                Editar
                                            </button>
                                            <button
                                                onClick={() => handleDeleteProduct(product.id)}
                                                className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-full transition-colors duration-200 shadow-md"
                                            >
                                                Excluir
                                            </button>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div>
                </div>
            )}

            {/* Reports Tab Content */}
            {activeTab === 'relatorios' && (
                <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-4xl">
                    <h2 className="text-3xl font-bold text-gray-700 mb-6 pb-3 border-b-2 border-blue-200">
                        Relatório de Vendas Semanais
                    </h2>
                    {sales.length === 0 ? (
                        <p className="text-gray-500">Nenhuma venda registrada ainda.</p>
                    ) : (
                        <div className="max-h-96 overflow-y-auto">
                            <table className="min-w-full bg-white rounded-lg overflow-hidden shadow-md">
                                <thead className="bg-blue-500 text-white">
                                    <tr>
                                        <th className="py-3 px-4 text-left text-sm font-semibold uppercase">Data</th>
                                        <th className="py-3 px-4 text-left text-sm font-semibold uppercase">Total Vendido</th>
                                    </tr>
                                </thead>
                                <tbody className="divide-y divide-gray-200">
                                    {getWeeklySales().map(([date, total]) => (
                                        <tr key={date} className="hover:bg-gray-50">
                                            <td className="py-3 px-4 text-gray-800">
                                                {date}
                                            </td>
                                            <td className="py-3 px-4 text-green-600 font-bold">R$ {total.toFixed(2)}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                            {getWeeklySales().length === 0 && (
                                <p className="text-gray-500 mt-4">Nenhuma venda na última semana.</p>
                            )}
                        </div>
                    )}

                    <h3 className="text-2xl font-bold text-gray-700 mt-10 mb-4 pb-2 border-b-2 border-blue-200">
                        Todas as Vendas
                    </h3>
                    {sales.length === 0 ? (
                        <p className="text-gray-500">Nenhuma venda registrada ainda.</p>
                    ) : (
                        <div className="max-h-96 overflow-y-auto">
                            <table className="min-w-full bg-white rounded-lg overflow-hidden shadow-md">
                                <thead className="bg-blue-500 text-white">
                                    <tr>
                                        <th className="py-3 px-4 text-left text-sm font-semibold uppercase">Data/Hora</th>
                                        <th className="py-3 px-4 text-left text-sm font-semibold uppercase">Itens</th>
                                        <th className="py-3 px-4 text-left text-sm font-semibold uppercase">Total</th>
                                        <th className="py-3 px-4 text-left text-sm font-semibold uppercase">Método</th>
                                    </tr>
                                </thead>
                                <tbody className="divide-y divide-gray-200">
                                    {sales.map(sale => (
                                        <tr key={sale.id} className="hover:bg-gray-50">
                                            <td className="py-3 px-4 text-gray-800">
                                                {sale.timestamp.toDate().toLocaleString('pt-BR')}
                                            </td>
                                            <td className="py-3 px-4 text-gray-700">
                                                {sale.items.map(item => (
                                                    <p key={item.productId}>{item.name} (x{item.quantity})</p>
                                                ))}
                                            </td>
                                            <td className="py-3 px-4 text-green-600 font-bold">R$ {sale.total.toFixed(2)}</td>
                                            <td className="py-3 px-4 text-gray-700">{sale.paymentMethod}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default App;
